import React, { useState } from "react";
import "./App.css";

function App() {
  // let [count, setCount] = React.useState(0);

  // const clickPlus = () => {
  //   setCount(count + 1);
  // };

  // const clickMinus = () => {
  //   setCount(count - 1);
  // };
  // const clickDelete = () => {
  //   setCount((count = 0));
  // };
  const [value, setValue] = useState("");
  const [inputHandler, SetInputHandler] = useState([]);

  const changeHandler = (e) => {
    setValue(e.target.value);
  };

  const savingValue = () => {
    SetInputHandler([
      ...inputHandler,
      {
        title: value,
        id: Math.random(),
      },
    ]);
    setValue("");
  };
  const deleteHandler=(id)=>{
    const filtered = inputHandler.filter((item)=> item.id !== id)
    SetInputHandler(filtered)
  }
  return (
    <div className="App">
      <input value={value} onChange={changeHandler} type="text" />
      <button onClick={savingValue}>Save</button>
      {inputHandler.map((item) => (
        <li>{item.title} <button onClick={(id)=>deleteHandler(item.id)}>Delete</button></li>
      ))}

      {/* <h2>Counter:</h2>
